<?php//echo '执行时间:' . Yii::getLogger ()->getExecutionTime () . '<br>';//echo '内存使用:' . convert ( memory_get_usage ( true ) );function convert($size) {	$unit = array (			'b',			'kb',			'mb',			'gb',			'tb',			'pb' 	);	return @round ( $size / pow ( 1024, ($i = floor ( log ( $size, 1024 ) )) ), 2 ) . ' ' . $unit [$i];}?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><meta http-equiv="Content-Type" Content="text/html" ; charset="UTF-8">	<head><title>测试程序</title><script	src="http://ajax.googleapis.com/ajax/libs/jquery/1.4.2/jquery.min.js"></script><script>var show = function(showid,commentid){	$("#shwo_"+showid).toggle();}function Support(commentid,divid){ 	var url = "http://www.dw.com/dwnews/index.php?r=comment/support&commentid="+commentid+"&callback=?";  		jQuery.getJSON(url, function(data){  		$("#support_show_"+divid).html('['+data.support+']');		//<span id=support_show_'.$div_id.'>[0]</span>		//alert("support:" + data.support + ", Price:" + data.price);  	}); }function post(id,commentid){	if(id=='post'){	var content = encodeURIComponent($("#content").val());	var authorid = $("#authorid").val();	var articleid = $("#articleid").val();	var url = "http://www.dw.com/dwnews/index.php?r=comment/post&doitem=post&content="+content+"&authorid="+authorid+"&articleid="+articleid+"&callback=?";  	jQuery.getJSON(url, function(data){  		if(data.post=='success'){           alert('评论完成');		}else{           alert('评论有问题');		}	}); 	}else{		var content = encodeURIComponent($("#content_"+id).val());		var authorid = $("#authorid_"+id).val();		var commentid = $("#commentid_"+id).val();		var articleid = $("#articleid_"+id).val();		var topid = $("#id_"+id).val();		var url = "http://www.dw.com/dwnews/index.php?r=comment/post&doitem=ajaxdo&topid="+topid+"&commentid="+commentid+"&content="+content+"&authorid="+authorid+"&